mapper.update:
UPDATE users
username = #{user.username}, password = #{user.password}, nickname = #{user.nickname}, email = #{user.email}, avatar = #{user.avatar}, school = #{user.school}, major = #{user.major}, bio = #{user.bio},
WHERE id = #{user.id}

mapper.select:
SELECT COUNT(*) > 0
FROM users
WHERE username = #{username}
AND id != #{excludeUserId}
AND is_deleted = 0
